mapper.select:
select

from mmall_staff
where 1 = 1

        and id = #{id}


        and role = #{role}


        and status = #{status}


    and username like #{username}

ORDER BY status asc

mapper.update:
update mmall_staff
set status = #{status}
where id = #{id}

update mmall_staff
set main_image = #{mainImage}
where id = #{id}
