mapper.select:
select

    from mmall_user
where username = #{username} and password = #{password}

mapper.update:
update mmall_user
set password = #{password}, update_time = now()
where username = #{username}

mapper.select:
select
from mmall_user -- level 1 为普通， role 0 为学员 member status = 2 为进行中 where level !=1 and role = 0
and wechat like #{wechat}
